  const formRef = React.useRef<UseFormReturn>(null);
  const steps = React.useMemo<(FormStepProps & {
    step: () => JSX.Element;
  })[]>(() => [{
    name: 'profile',
    title: 'Profile',
    onSubmit: async (data, stepper) => {
      // check email validity
      console.log(data, stepper);
      if (data.email === 'exists@saas-ui.dev') {
        formRef.current?.setError('email', {
          message: 'This email address is already registered.'
        });
        throw new Error('Email exists already');
      }
    },
    step: () => {
      const watch = useWatch();
      console.log(watch);
      return <FormLayout>\r
              <Field name="name" label="Name" />\r
              <Field name="email" label="Email" autoComplete="off" />\r
              <StepperNav />\r
            </FormLayout>;
    }
  }, {
    name: 'password',
    title: 'Password',
    step: () => {
      const form = useFormContext();
      console.log(form.getValues('email'));
      return <FormLayout>\r
              <Field name="password" label="Password" type="password" autoFocus autoComplete="off" />\r
              <StepperNav />\r
            </FormLayout>;
    }
  }, {
    name: 'confirmation',
    title: 'Confirmation',
    step: () => {
      return <FormLayout>\r
              <Text>Please confirm that your information is correct.</Text>\r
              <PropertyList>\r
                <Property label="Name" value={<FormValue name="name" />} />\r
                <Property label="Email" value={<FormValue name="email" />} />\r
              </PropertyList>\r
              <StepperNav />\r
            </FormLayout>;
    }
  }], []);
  return <>\r
      <StepForm formRef={formRef} defaultValues={{
      name: '',
      email: '',
      password: ''
    }} onSubmit={onSubmit}>\r
        <FormLayout>\r
          <FormStepper>\r
            {steps.map(({
            step: Step,
            name,
            ...rest
          }) => <FormStep key={name} name={name} {...rest}>\r
                <Step />\r
              </FormStep>)}\r
\r
            <StepsCompleted>\r
              <Alert status="success">\r
                <AlertIcon />\r
                <Box>\r
                  <AlertTitle>Thanks for signing up</AlertTitle>\r
                  <AlertDescription width="full">\r
                    Check your inbox to confirm your email address.\r
                  </AlertDescription>\r
                </Box>\r
              </Alert>\r
            </StepsCompleted>\r
          </FormStepper>\r
        </FormLayout>\r
      </StepForm>\r
    </>;
